
/**
 * Module dependencies.
 */

var inherit = require('..');

describe('inherit(a, field_size)', function(){
  it('should inherit field_size\'s prototype', function(){
    function Loki(){}
    function Animal(){}

    Animal.prototype.species = 'unknown';

    inherit(Loki, Animal);

    var loki = new Loki;
    loki.species.should.equal('unknown');
    loki.constructor.should.equal(Loki);
  })
})